import { useState } from 'react';
import { motion } from 'framer-motion';
import { getToken } from '../auth';

export default function SubmitProposal() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState(''); // New state for proposed date
  const [message, setMessage] = useState('');

  const handleSubmit = async () => {
    try {
      const res = await fetch('/api/proposals', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${getToken()}`
        },
        body: JSON.stringify({ title, description, date })
      });

      const data = await res.json();
      if (res.ok) {
        setMessage('ðŸŽ‰ Proposal submitted successfully!');
        setTitle('');
        setDescription('');
        setDate('');
      } else {
        setMessage(data.message || 'Error submitting proposal');
      }
    } catch (err) {
      console.error(err);
      setMessage('Submission failed');
    }
  };

  return (
    <motion.div 
      className="bg-gray-900 min-h-screen flex justify-center items-center p-4"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <div className="bg-gray-800 p-8 rounded-lg shadow-lg w-full max-w-md text-white">
        <h2 className="text-2xl font-bold mb-4 text-center">Submit Proposal</h2>

        {message && (
          <motion.div 
            className="text-center text-green-400 mb-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
          >
            {message}
          </motion.div>
        )}

        <input 
          value={title} 
          onChange={(e) => setTitle(e.target.value)} 
          placeholder="Title" 
          className="block w-full p-2 mb-4 bg-gray-700 rounded"
        />
        <textarea 
          value={description} 
          onChange={(e) => setDescription(e.target.value)} 
          placeholder="Description" 
          className="block w-full p-2 mb-4 bg-gray-700 rounded"
        />
        <input 
          type="date"
          value={date} 
          onChange={(e) => setDate(e.target.value)} 
          className="block w-full p-2 mb-4 bg-gray-700 rounded"
        />
        <button 
          onClick={handleSubmit} 
          className="bg-blue-500 px-4 py-2 rounded w-full hover:bg-blue-600 transition"
        >
          Submit
        </button>
      </div>
    </motion.div>
  );
}
