import { useState } from 'react';
import { getToken } from '../auth';
import { motion, AnimatePresence } from 'framer-motion';

export default function SubmitProposal() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');
  const [showPopup, setShowPopup] = useState(false);

  const handleSubmit = async () => {
    try {
      const res = await fetch('/api/proposals', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${getToken()}`
        },
        body: JSON.stringify({ title, description, date, username: "YourUsername" }) // Replace with dynamic username
      });

      const data = await res.json();
      if (res.ok) {
        setTitle('');
        setDescription('');
        setDate('');
        setShowPopup(true);
        setTimeout(() => setShowPopup(false), 2000); // Auto-close popup after 2 sec
      } else {
        alert(data.message || 'Error submitting proposal');
      }
    } catch (err) {
      console.error(err);
      alert('Submission failed');
    }
  };

  return (
    <motion.div 
      className="bg-gray-900 min-h-screen flex justify-center items-center text-white"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <div className="bg-gray-800 p-8 rounded-xl shadow-xl w-96">
        <h2 className="text-2xl mb-4 text-center font-bold">Submit Proposal</h2>
        <input 
          value={title} 
          onChange={(e) => setTitle(e.target.value)} 
          placeholder="Title" 
          className="block w-full mb-3 p-2 rounded bg-gray-700"
        />
        <textarea 
          value={description} 
          onChange={(e) => setDescription(e.target.value)} 
          placeholder="Description" 
          className="block w-full mb-3 p-2 rounded bg-gray-700"
        />
        <input 
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)} 
          className="block w-full mb-3 p-2 rounded bg-gray-700"
        />
        <button 
          onClick={handleSubmit} 
          className="bg-blue-500 px-4 py-2 rounded w-full hover:bg-blue-600 transition"
        >
          Submit
        </button>

        {/* Success Popup */}
        <AnimatePresence>
          {showPopup && (
            <motion.div 
              initial={{ opacity: 0, scale: 0.8 }} 
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 0.8 }}
              transition={{ duration: 0.3 }}
              className="fixed top-10 right-10 bg-green-500 text-white px-4 py-2 rounded shadow-lg"
            >
              Proposal Submitted!
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </motion.div>
  );
}
