import { useState } from 'react';
import { getToken } from '../auth';
import { motion } from 'framer-motion';

export default function SubmitProposal() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async () => {
    try {
      const res = await fetch('/api/proposals', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${getToken()}`
        },
        body: JSON.stringify({ title, description, username: "YourUsername" }) // Replace username dynamically if needed
      });

      const data = await res.json();
      if (res.ok) {
        setSuccess(true);
        setError('');
        setTitle('');
        setDescription('');
      } else {
        setSuccess(false);
        setError(data.message || 'Error submitting proposal');
      }
    } catch (err) {
      console.error(err);
      setSuccess(false);
      setError('Submission failed');
    }
  };

  return (
    <motion.div 
      className="bg-gray-900 min-h-screen flex justify-center items-center text-white"
      initial={{ opacity: 0, y: -20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <div className="bg-gray-800 p-8 rounded-xl shadow-xl w-96 text-center">
        <h2 className="text-2xl font-bold mb-4">Submit Your Proposal</h2>
        
        {success && <p className="text-green-400 mb-4">Proposal submitted successfully!</p>}
        {error && <p className="text-red-400 mb-4">{error}</p>}

        <input 
          type="text"
          placeholder="Proposal Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="block w-full p-3 mb-4 bg-gray-700 rounded focus:outline-none focus:ring focus:ring-blue-400"
        />
        <textarea
          placeholder="Proposal Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="block w-full p-3 mb-4 bg-gray-700 rounded focus:outline-none focus:ring focus:ring-blue-400"
          rows="4"
        ></textarea>
        <button 
          onClick={handleSubmit}
          className="bg-blue-500 hover:bg-blue-600 transition px-4 py-2 rounded w-full font-semibold"
        >
          Submit
        </button>
      </div>
    </motion.div>
  );
}
