import { motion } from "framer-motion";
import { useState } from "react";

const PRE_ASSIGNED_CODE = "ASU123"; // Change this to your actual code

export default function Register() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [code, setCode] = useState(""); // Store the registration code
  const [error, setError] = useState("");

  const handleRegister = () => {
    if (!username || !password) {
      setError("Username and password are required.");
      return;
    }

    // Validate pre-assigned code
    if (code !== PRE_ASSIGNED_CODE) {
      setError("Invalid registration code. Only members can register.");
      return;
    }

    // Save user and redirect
    localStorage.setItem("user", username);
    window.location.href = "/"; // Redirect to home after registering
  };

  return (
    <motion.div 
      className="bg-gray-900 min-h-screen flex justify-center items-center text-white"
      initial={{ opacity: 0, y: -20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <div className="bg-gray-800 p-6 rounded-lg shadow-lg w-80">
        <h2 className="text-2xl font-bold mb-4">Register</h2>
        {error && <p className="text-red-500 text-sm mb-2">{error}</p>}
        <input 
          type="text" 
          placeholder="Username" 
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="block w-full p-2 mb-2 bg-gray-700 rounded"
        />
        <input 
          type="password" 
          placeholder="Password" 
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="block w-full p-2 mb-2 bg-gray-700 rounded"
        />
        <input 
          type="text" 
          placeholder="Pre-Assigned Code" 
          value={code}
          onChange={(e) => setCode(e.target.value)}
          className="block w-full p-2 mb-2 bg-gray-700 rounded"
        />
        <button 
          onClick={handleRegister} 
          className="bg-blue-500 px-4 py-2 rounded w-full"
        >
          Sign Up
        </button>
      </div>
    </motion.div>
  );
}
